export const citiesWithImage = cities;

export function getCityName(language: string, city: City) {
  if (language === "fr") {
    return frenchCityNames[city.code];
  }
  return city.name;
}

export function sanitizeCityName(cityName: string): string {
  return cityName
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")
    .replace(/[- '()]/g, "")
    .toLowerCase();
}
